export const drawChartsAsync = async (chartCanvasRefs, charts) => {
  for (let i = 0; i < charts.length; i++) {
    const ctx = chartCanvasRefs[i].getContext('2d');
    const chart = charts[i];
    
    if (Chart.getChart(ctx)) {
      Chart.getChart(ctx)?.destroy();
    }
    
    await drawChartAsync(ctx, chart);
  }
};
const drawChartAsync = (ctx, chartData) => {
  return new Promise(resolve => {
    setTimeout(() => {
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: chartData.time,
          datasets: [{
            label: 'ping',
            data: chartData.ping,
            borderColor: 'rgb(75, 192, 192)',
            backgroundColor: 'rgb(75, 192, 192)',
            borderWidth: 1,
            fill: false,
            pointRadius : 0
          }]
        },
        options: {    
          plugins: {
              legend: {
                  position: 'bottom', // 범례 위치
                  align: 'start' // 범례 수직 정렬
              }
          },
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'day',
                displayFormats: {
                  day: 'MM/dd'
                }
              }
            },
            y: {
              min: 0,
              max: 60000
            }
          }
        },
        plugins: {
          afterDraw: resolve
        }
      });
    }, 0);
  });
};
