import Chart from 'chart.js/auto';

const drawChart = (ctx, time, ping) => {
    
   console.log("drawChart");
    new Chart(ctx, {
      type: 'line',
      data: {
        labels: time.value,
        datasets: [{
          label: 'ping',
          data: ping.value,
          borderColor: 'rgb(75, 192, 192)',
          borderWidth: 1,
          fill: false,
          pointRadius : 0
        }]
      },
      options: {    
        scales: {
          x: {
            type: 'time',
            time: {
              unit: 'day',
              displayFormats: {
                day: 'MM/dd'
              }
            }
          },
          y: {
            min: 0,
            max: 60000
          }
        }
      }
    });
  };


  export default drawChart;
