import Chart from 'chart.js/auto';
import 'chartjs-adapter-date-fns';
import { ref, onMounted } from 'vue';
import axios from 'axios';
export default {
  name: 'TimeChart',
  setup() {
    const time = ref([]);

    // const getData = async () => {
    //     const res = await axios.get(`/get/chartData`);
    //     const data = res.data;
    //     console.log("data:"+data.ping);
    // };

    const getData = async () => {
      await axios.get("/get/chartData").then(result => {
        result.data.forEach(item => {
          //console.log(item.time);
          time.value = item.time;
          console.log(time.value);
          const chartCanvas = ref(null);
          onMounted(() => {
            const ctx = chartCanvas.value.getContext('2d');
            new Chart(ctx, {
              type: 'line',
              data: {
                labels: time.value,
                datasets: [{
                  label: 'Data',
                  data: [12, 19, 3, 5, 2, 16, 20],
                  borderColor: 'rgb(75, 192, 192)',
                  borderWidth: 1,
                  fill: false
                }]
              },
              options: {
                scales: {
                  x: {
                    type: 'time',
                    time: {
                      unit: 'day'
                    }
                  }
                }
              }
            });
          });
        });
      });
    };
    getData();
    return {
      chartCanvas,
      getData
    };
  }
};
