    const date = new Date();
    const startDate = ref(new Date(date.getFullYear(), date.getMonth(), 1));
    const endDate = ref(new Date());
    const isLoading = ref(false);
    const chartCanvasRefs = ref([]);
    const charts = ref([]);
    const visibleCharts = ref([]);
    const currentIndex = ref(0);
    const groupSize = 5;
    const handleDateChange = (event, dateRef) => {
      const dateString = event.target.value;
      if (!dateString) return;
      const [year, month, day] = dateString.split('-').map(Number);
      dateRef.value = new Date(year, month - 1, day);
    };
    const handleStartDateChange = event => handleDateChange(event, startDate);
    const handleEndDateChange = event => handleDateChange(event, endDate);
    const getData = async () => {
      const formattedStartDate = moment(startDate.value).format('YYYY-MM-DD');
      const formattedEndDate = moment(endDate.value).format('YYYY-MM-DD');
      if (formattedStartDate > formattedEndDate) {
        alert("시작날짜는 끝날짜보다 작아야 합니다.");
        return;
      }
      isLoading.value = true;
      try {
        const response = await axios.get('/get/chartData', {
          params: {
            startDate: formattedStartDate,
            endDate: formattedEndDate
          }
        });
        const data = response.data;
        const time = data.map(item => new Date(item.time).getTime());
        const ping = data.map(item => item.ping);
        const serverName = data.map(item => item.serverName);
        charts.value = Array.from({
          length: 20
        }, () => ({
          time: [...time],
          ping: [...ping],
          serverName: serverName[0] // Simplification
        }));
        currentIndex.value = 0;
        loadMoreCharts();
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        isLoading.value = false;
      }
    };
    const loadMoreCharts = async () => {
      const endIndex = currentIndex.value + groupSize;
      const newCharts = charts.value.slice(currentIndex.value, endIndex);
      visibleCharts.value.push(...newCharts);
      await nextTick();
      await drawChartsInGroups(newCharts);
      currentIndex.value = endIndex;
    };
    const drawChartAsync = (context, time, ping) => {
      return new Promise(resolve => {
        requestAnimationFrame(() => {
          drawChart(context, time, ping);
          resolve();
        });
      });
    };
    const drawChartsInGroups = async chartsToDraw => {
      const groupPromises = chartsToDraw.map((chart, index) => {
        const canvas = chartCanvasRefs.value[currentIndex.value + index];
        if (canvas) {
          const context = canvas.getContext('2d');
          return drawChartAsync(context, chart.time, chart.ping);
        }
      });
      await Promise.all(groupPromises);
    };
    const handleScroll = () => {
      const bottomReached = window.innerHeight + window.scrollY >= document.documentElement.scrollHeight - 100;
      if (bottomReached && currentIndex.value < charts.value.length) {
        loadMoreCharts();
      }
    };
    onMounted(() => {
      getData();
      window.addEventListener('scroll', handleScroll);
    });
    onBeforeUnmount(() => {
      window.removeEventListener('scroll', handleScroll);
    });
    return {
      startDate,
      endDate,
      handleStartDateChange,
      handleEndDateChange,
      getData,
      isLoading,
      chartCanvasRefs,
      visibleCharts
    };
  }
